const express = require('express');
const helmet = require('helmet');
const app = express();

app.use(helmet()); // Manual security setup
app.get('/', (req, res) => res.send('Hello World'));

app.listen(3000);

@Controller()
export class AppController {
  @Get()
  getHello(): string {
    return 'Hello World';
  }
}
// Dependency injection and modular structure are built in.
